import React, { useContext, useState } from "react";
import CssBaseline from '@mui/material/CssBaseline';
import Container from '@mui/material/Container';
import { ColorModeContext } from "../theme";
import { ThemeProvider, useTheme } from "@mui/material";
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import AddIcon from '@mui/icons-material/Add';


const Home = () => {
    const theme = useTheme();
    const colorMode = useContext(ColorModeContext);

    const [inputCount, setInputCount] = useState(0); // İlk inputtaki sayı
    const [sharedValue, setSharedValue] = useState(""); // İkinci inputtaki değer
    const [generatedInputs, setGeneratedInputs] = useState([]); // Oluşturulan inputlar

    // Butona tıklanınca yeni inputları oluştur
    const handleAddInputs = () => {
        const count = parseInt(inputCount) || 0;
        const value = sharedValue;

        // Girilen sayı kadar input oluştur
        const newInputs = Array.from({ length: count }, () => value);
        setGeneratedInputs(newInputs);
    };





    return (
        <ColorModeContext.Provider value={colorMode}>
            <ThemeProvider theme={theme}>
                <CssBaseline />
                <Container maxWidth="md">
                    <Box sx={{
                        flexGrow: 1, backgroundColor: "#717171", p: 5

                    }}>
                        <Grid container spacing={2}>
                            <Grid item xs={4}>
                                <TextField
                                    required
                                    id="outlined-required"
                                    label="Number"
                                    defaultValue="0"
                                    fullWidth={true}
                                    value={inputCount}
                                    onChange={(e) => setInputCount(e.target.value)}
                                />
                            </Grid>
                            <Grid item xs={4}>
                                <TextField
                                    required
                                    id="outlined-required"
                                    label="Number"
                                    defaultValue="0"
                                    fullWidth={true}
                                    value={sharedValue}
                                    onChange={(e) => setSharedValue(e.target.value)}
                                />
                            </Grid>
                            <Grid item xs={4} display="flex" justifyContent="flex-end">

                                <Button variant="outlined" startIcon={<AddIcon />} fullWidth={true} size="small" 
                                onClick={handleAddInputs}>
                                    ADD
                                </Button>
                            </Grid>

                        </Grid>
                        <Box mt={4}>
          <Grid container spacing={2}>
            {generatedRows.map((row, index) => (
              <React.Fragment key={index}>
                {/* Her satırdaki 1. input */}
                <Grid item xs={6}>
                  <TextField
                    fullWidth
                    label={`Satır ${index + 1} - Değer 1`}
                    value={row.value1}
                    onChange={(e) =>
                      handleInputChange(index, "value1", e.target.value)
                    }
                  />
                </Grid>

                {/* Her satırdaki 2. input */}
                <Grid item xs={6}>
                  <TextField
                    fullWidth
                    label={`Satır ${index + 1} - Değer 2`}
                    value={row.value2}
                    onChange={(e) =>
                      handleInputChange(index, "value2", e.target.value)
                    }
                  />
                </Grid>
              </React.Fragment>
            ))}
          </Grid>
        </Box>
                    </Box>


                </Container>
            </ThemeProvider>
        </ColorModeContext.Provider>
    )

}

export default Home;
