import React, { useContext, useState } from "react";
import CssBaseline from '@mui/material/CssBaseline';
import Container from '@mui/material/Container';
import { ColorModeContext } from "../theme";
import { ThemeProvider, useTheme } from "@mui/material";
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import AddIcon from '@mui/icons-material/Add';


const Home = () => {
    const theme = useTheme();
    const colorMode = useContext(ColorModeContext);

    const [inputCount, setInputCount] = useState(0); // Dinamik input sayısı
    const [sharedValue, setSharedValue] = useState(""); // Tüm inputlarda gösterilecek değer
    const [inputs, setInputs] = useState([]); // Dinamik input değerlerini saklamak için


    // 1. input değişikliğini yönetir
  const handleInputCountChange = (e) => {
    const count = parseInt(e.target.value) || 0;
    setInputCount(count);

    // Yeni inputlar ekler veya kaldırır
    const newInputs = Array.from({ length: count }, (_, i) => inputs[i] || "");
    setInputs(newInputs);
  };

  // 2. input değişikliğini yönetir
  const handleSharedValueChange = (e) => {
    const value = e.target.value;
    setSharedValue(value);

    // Tüm input değerlerini günceller
    setInputs((prevInputs) => prevInputs.map(() => value));
  };

  // Dinamik input değişikliğini yönetir
  const handleDynamicInputChange = (index, value) => {
    setInputs((prevInputs) =>
      prevInputs.map((input, i) => (i === index ? value : input))
    );
  };


    return (
        <ColorModeContext.Provider value={colorMode}>
        <ThemeProvider theme={theme}>
          <CssBaseline />
          <Container maxWidth="md">
            <Box sx={{ flexGrow: 1, backgroundColor: "#717171", p: 5 }}>
              <Grid container spacing={2}>
                {/* İlk input: Dinamik input sayısını belirler */}
                <Grid item xs={4}>
                  <TextField
                    required
                    label="Number of Inputs"
                    type="number"
                    defaultValue="0"
                    fullWidth
                    onChange={handleInputCountChange}
                  />
                </Grid>

                {/* İkinci input: Tüm inputların değerini belirler */}
                <Grid item xs={4}>
                  <TextField
                    required
                    label="Shared Value"
                    value={sharedValue}
                    fullWidth
                    onChange={handleSharedValueChange}
                  />
                </Grid>

                {/* Ekleme butonu */}
                <Grid item xs={4} display="flex" justifyContent="flex-end">
                  <Button
                    variant="outlined"
                    startIcon={<AddIcon />}
                    size="small"
                    fullWidth
                    disabled
                  >
                    ADD
                  </Button>
                </Grid>

                {/* Dinamik inputlar */}
                {inputs.map((value, index) => (
                  <Grid item xs={12} key={index}>
                    <TextField
                      fullWidth
                      label={`Input ${index + 1}`}
                      value={value}
                      onChange={(e) =>
                        handleDynamicInputChange(index, e.target.value)
                      }
                    />
                  </Grid>
                ))}
              </Grid>
            </Box>
          </Container>
        </ThemeProvider>
      </ColorModeContext.Provider>
    )

}

export default Home;
