import React from "react";
import { createBrowserRouter, RouterProvider } from "react-router-dom";
import Home from "./Pages/Home";
import Navbar from "./Pages/Navbar";
import { ColorModeContext, useMode } from "./theme";
import { CssBaseline, ThemeProvider } from "@mui/material";

// Rotaları ve gelecekteki bayrağı etkinleştirme
const router = createBrowserRouter(
  [
    {
      path: "/",
      element: <Home />,
    },
    // Splat (yıldız) rotalarını burada kullanabilirsiniz
    {
      path: "about/*", // Splat route örneği
      element: <About />,
    },
  ],
  {
    future: {
      v7_relativeSplatPath: true, // Yeni yol çözümlemesi için bayrağı etkinleştiriyoruz
    },
  }
);

const App = () => {
  const [theme, colorMode] = useMode();
  return (
    <div>
      <ColorModeContext.Provider value={colorMode}>
        <ThemeProvider theme={theme}>
          <CssBaseline />
          <RouterProvider router={router}>
            <Navbar /> {/* Navbar her sayfada görünür */}
          </RouterProvider>
        </ThemeProvider>
      </ColorModeContext.Provider>
    </div>
  );
};

export default App;
