import React, { useContext, useState, useEffect } from "react";
import CssBaseline from '@mui/material/CssBaseline';
import Container from '@mui/material/Container';
import { ColorModeContext } from "../theme";
import { ThemeProvider, useTheme } from "@mui/material";
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import AddIcon from '@mui/icons-material/Add';

const Home = () => {
    const theme = useTheme();
    const colorMode = useContext(ColorModeContext);

    const [inputCount, setInputCount] = useState(0); // İlk inputtaki sayı
    const [sharedValue, setSharedValue] = useState(""); // İkinci inputtaki değer
    const [generatedRows, setGeneratedRows] = useState([]); // Oluşturulan satırlar

    // sharedValue değiştiğinde mevcut satırların value2'sini güncelle
    useEffect(() => {
        setGeneratedRows((prevRows) =>
            prevRows.map((row) => ({ ...row, value2: sharedValue }))
        );
    }, [sharedValue]);

    // ADD butonuna basıldığında yeni satır ekleme
    const handleAddRow = () => {
        const count = parseInt(inputCount) || 0;
        const newRows = Array.from({ length: count }, () => ({ value1: "", value2: sharedValue }));
        setGeneratedRows((prevRows) => [...prevRows, ...newRows]);
    };

    // Tek tek satırdaki inputların değerlerini güncellemek için
    const handleInputChange = (index, field, newValue) => {
        const updatedRows = [...generatedRows];
        updatedRows[index][field] = newValue;
        setGeneratedRows(updatedRows);
    };

    return (
        <ColorModeContext.Provider value={colorMode}>
            <ThemeProvider theme={theme}>
                <CssBaseline />
                <Container maxWidth="md">
                    <Box sx={{
                        flexGrow: 1, backgroundColor: "#717171", p: 5

                    }}>
                        <Grid container spacing={2}>
                            <Grid item xs={4}>
                                <TextField
                                    required
                                    id="outlined-required"
                                    label="Number"
                                    fullWidth={true}
                                    value={inputCount}
                                    onChange={(e) => setInputCount(e.target.value)}
                                />
                            </Grid>
                            <Grid item xs={4}>
                                <TextField
                                    required
                                    id="outlined-required"
                                    label="Value"
                                    fullWidth={true}
                                    value={sharedValue}
                                    onChange={(e) => setSharedValue(e.target.value)}
                                />
                            </Grid>
                            <Grid item xs={4} display="flex" justifyContent="flex-end">
                                <Button
                                    variant="outlined"
                                    startIcon={<AddIcon />}
                                    fullWidth={true}
                                    size="small"
                                    onClick={handleAddRow}
                                >
                                    ADD
                                </Button>
                            </Grid>
                        </Grid>
                        <Box mt={4}>
                            <Grid container spacing={2}>
                                {generatedRows.map((row, index) => (
                                    <React.Fragment key={index}>
                                        {/* Her satırdaki 1. input */}
                                        <Grid item xs={6}>
                                            <TextField
                                                fullWidth
                                                label={`Row ${index + 1} - Value 1`}
                                                value={row.value1}
                                                onChange={(e) =>
                                                    handleInputChange(index, "value1", e.target.value)
                                                }

                                            />
                                        </Grid>

                                        {/* Her satırdaki 2. input */}
                                        <Grid item xs={6}>
                                            <TextField
                                                fullWidth
                                                label={`Row ${index + 1} - Value 2`}
                                                value={row.value2}
                                                onChange={(e) =>
                                                    handleInputChange(index, "value2", e.target.value)
                                                }
                                            />
                                        </Grid>
                                    </React.Fragment>
                                ))}
                            </Grid>
                        </Box>
                    </Box>
                </Container>
            </ThemeProvider>
        </ColorModeContext.Provider>
    );
};

export default Home;
