import React from "react";
import { createBrowserRouter, RouterProvider } from "react-router-dom"; // createBrowserRouter kullanımı
import Home from "./Pages/Home";
import { ColorModeContext, useMode } from "./theme";
import { CssBaseline, ThemeProvider } from "@mui/material";
import Navbar from "./Pages/Navbar";

// Rotaları ve geçerli bayrağı etkinleştirme
const router = createBrowserRouter(
  [
    {
      path: "/",
      element: <Home />, // Ana sayfa
    },
  ],
  {
    future: {
      v7_relativeSplatPath: true, // Gelecek bayrağını etkinleştiriyoruz
    },
  }
);

const App = () => {
  const [theme, colorMode] = useMode();
  return (
    <div>
      <ColorModeContext.Provider value={colorMode}>
        <ThemeProvider theme={theme}>
          <CssBaseline />
          <RouterProvider router={router}> {/* RouterProvider kullanımı */}
            <Navbar /> {/* Navbar'ı burada ekliyoruz */}
          </RouterProvider>
        </ThemeProvider>
      </ColorModeContext.Provider>
    </div>
  );
};

export default App;
